INSERT INTO `#__content` VALUES ('43', 'Ciencías Básicas', 'ciencias-basicas', '', '<p style=\"text-align: justify;\"><img src=\"images/stories/ciencias%20basicas.jpg\" border=\"0\" alt=\"ciencias basicas\" width=\"155\" height=\"125\" style=\"margin-left: 15px; margin-right: 15px; border: 0px none; float: right;\" /></p>\r\n<p>Objetivo General</p>\r\n<p style=\"text-align: justify;\">En esta academia los PTC colaboradores realizan las siguientes funciones fundamentales:</p>\r\n<ul style=\"text-align: justify;\">\r\n<li>Crear y actualizar los contenidos de las asignaturas de la Academia.</li>\r\n<li>Valorar la calidad del material didáctico desarrollado por los profesores colaboradores en la Academia.</li>\r\n<li>Proponer profesores para la impartición de las asignaturas de la Academia.</li>\r\n<li style=\"text-align: justify;\">Asesoran a los estudiantes que estén cursando asignaturas de la Academia.</li>\r\n</ul>\r\n', '\r\n<p> </p>\r\n<h3 style=\"text-align: justify;\">Responsable</h3>\r\n<hr style=\"text-align: justify;\" />\r\n<p style=\"text-align: justify;\">Dr. Victor Manuel Romero Medina</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<h3 style=\"text-align: justify;\">Miembros</h3>\r\n<hr style=\"text-align: justify;\" />\r\n<p style=\"text-align: justify;\">Dra. Estela Cerezo Acevedo</p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\">Dra. Marina Isabel García Rosas</p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"> </p>\r\n<h3 style=\"text-align: justify;\">Profesores Invitados</h3>\r\n<hr style=\"text-align: justify;\" />\r\n<p style=\"text-align: justify;\">M. C. Marcelo Hugo Sánchez Núñez</p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\">Ing. Juan Francisco Bárcenas Graniel</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<h3 style=\"text-align: justify;\">Programa de Asignaturas</h3>\r\n<hr style=\"text-align: justify;\" />\r\n<table style=\"width: 570px; text-align: justify;\" border=\"0\">\r\n<tbody>\r\n<t