INSERT INTO `#__content` VALUES ('53', 'Laboratorio de Simulación de Sistemas Complejos', 'laboratorio-de-simulacion-de-sistemas-complejos', '', '<p style=\"text-align: justify;\"><strong><img src=\"images/stories/laboratorio de simulacin de sistemas complejos.jpg\" border=\"0\" alt=\"Laboratorio de Simulación de Sistemas Complejos\" width=\"242\" height=\"165\" style=\"margin-left: 15px; margin-right: 15px; float: right;\" />El Laboratorio de Simulación de Sistemas Complejos (LASSC)</strong> se encuentra ubicado en la planta baja del edificio B. En éste se  llevan a cabo actividades de investigación y desarrollo tecnológico  fomentando la creación de proyectos que beneficien a la sociedad de una  manera sustentable. La principal línea de investigación que persigue el  laboratorio es la aplicación de la Inteligencia Computacional para el  modelado de sistemas complejos,</p>\r\n', '\r\n<p>especialmente nos interesa la  observación de fenómenos emergentes de los procesos adaptativos y  auto-organizativos de dichos sistemas. LASSC proporciona la oportunidad a  estudiantes de los programas educativos de ingeniería, de colaborar y  realizar proyectos innovadores como parte de las asignaturas de  Seminario de Investigación y Proyecto Terminal. Algunas de las  herramientas con las que cuenta el laboratorio son computadoras IMACs,  robots autónomos Khepera III, incluidos algunos accesorios como sensor  de vision, etc., plataforma para trabajar con los Robots, servidor  MACPro con 8 núcleos, computadoras Dell y HP de alta precisión, tarjetas  DSP Starter Kit, entre otros.</p>\r\n<p> </p>\r\n<p> </p>\r\n<h2>Datos Generales</h2>\r\n<table border=\"0\" width=\"500\">\r\n<tbody>\r\n<tr style=\"background-color: #eeeeee;\">\r\n<td width=\"150\"><strong>Responsable</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Nombre</td>\r\n<td>Candelaria Sansores</td>\r\n</tr>\r\n<tr>\r\n<td>Extensión</td>\r\n<td>1001</td>\r\n</tr>\r\n<tr>\r\n<td>Correo</td>\r\n<td>csansores@ucaribe.edu.mx</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<table border=\"0\" width=\"500\">\r\n<tbody>\r\n<tr style=\"background-color: #eeeeee;\">\r\n<td width=\"150\"><strong>Informacion general</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Capacidad Maxima</td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td>Horario</td>\r\n<td>de 9 am a 2 pm y de 6pm a 10pm</td>\r\n</tr>\r\n<tr>\r\n<td>Reglamento</td>\r\n<td>Ver reglamento</td>\r\n</tr>\r\n<tr>\r\n<td>Ubicació</td>\r\n<td>Planta baja del edificio C</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<h2>Equipo con el que cuenta el laboratorio</h2>\r\n<ul>\r\n<li>Computadoras iMacs</li>\r\n<li>Robots autónomos Khepera III</li>\r\n<li>Servidor MACPRO con 8 núcleos</li>\r\n<li>Computadoras Dell y HP de alta precisión</li>\r\n<li>Tarjetas DSP Starter Kit</li>\r\n</ul>', '1', '6', '0', '36', '2011-12-14 05:12:25', '62', '', '2011-12-14 05:17:40', '62', '0', '0000-00-00 00:00:00', '2011-12-14 05:12:25', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', '2', '0', '3', '', '', '0', '2', 'robots=\nauthor=');
